import React from "react";
import "./PlaceInformation.css";

const PlaceInformation = (props) => {
    const { name, description, price, img } = props?.booking;
    return (

        <Container>
                <Row xs={1} md={2} lg={3} className="g-4 ">
                    {places.map((place) => (
                        <Col key={place._id}>
                            <Card className="card-style">
                                <Card.Img
                                    variant="top"
                                    src={place.img}
                                    className="card-img"
                                />
                                <Card.Body className="bg-dark  card-body ">
                                    <Card.Title className="card-title text-white fw-bold">
                                        {place.name}
                                    </Card.Title>
                                    <Card.Text className="card-para text-white">
                                        {place.description.slice(0, 100)}...
                                    </Card.Text>
                                    <Card.Text className="card-para text-white">
                                        ${place.price}
                                    </Card.Text>
                                    <Link to={`/placeorder/${place._id}`}>
                                        <button className="btn btn-info text-white fw-bold">
                                           Booking
                                        </button>
                                    </Link>
                                </Card.Body>
                            </Card>
                        </Col>
                    ))}
                </Row>
                
            </Container>
       
    );
};

export default PlaceInformation;





