import React from "react";
import { useForm } from "react-hook-form";
import "./AddNewBooking.css";
import bookingImg from '../../../src/img/BookingImg.png'
import { Col, Row } from "react-bootstrap";

const AddNewBooking = () => {
    const { register, handleSubmit, reset } = useForm();
    const onSubmit = (data) => {
        const uri = `https://quiet-everglades-89717.herokuapp.com/allbooking`;
        fetch(uri, {
            method: "POST",
            headers: {
                "content-type": "application/json",
            },
            body: JSON.stringify(data),
        })
            .then((res) => res.json())
            .then((data) => {
                if (data.insertedId) {
                    alert("added successfullay");
                    reset();
                }
            });
    };
    return (
        <Row>
            <Col>
            <div className="add-services">
            <h2 className="text-center text-info fw-bold mt-3">
                ADD NEW BOOKING
            </h2>

            <form onSubmit={handleSubmit(onSubmit)}>
                <input className="input-group"
                    {...register("name", {
                        required: true,
                        maxLength: 20,
                    })}
                    placeholder="Enter Your Name"
                    required
                />
                <textarea
                    {...register("description")}
                    placeholder="Enter your  description"
                    required
                />
                <input
                    type="number"
                    {...register("price")}
                    placeholder="Enter Your  Price"
                    required
                />
                <input {...register("img")} placeholder="Image Url" required />

                <input type="submit" value="submit" className="submit-form" />
            </form>
        </div>
            </Col>
            <Col>
             <img src={boo} alt="" />
            </Col>
        </Row>
    );
};

export default AddNewBooking;
