import Button from "@restart/ui/esm/Button";
import React from "react";
import { useRef } from "react";
// import { useState } from "react";

import useAuth from "../../../hooks/useAuth";
import "./PlaceForm.css";

const PlaceForm = (props) => {
    const nameRef = useRef();
    const emailRef = useRef();
    const placeNameRef = useRef();
    const descRef = useRef();
    const priceRef = useRef();
    const imgRef = useRef();
    const statusRef = useRef();
    const addressRef = useRef();
    const phoneRef = useRef();

    const { user } = useAuth();
    const { name, description, price, img } = props?.booking;

    const handleSubmit = (e) => {
        const name = nameRef.current.value;
        const email = emailRef.current.value;
        const placeName = placeNameRef.current.value;
        const placeDescriptions = descRef.current.value;
        const placePrice = priceRef.current.value;
        const placeImg = imgRef.current.value;
        const status = statusRef.current.value;
        const addres = addressRef.current.value;
        const phone = phoneRef.current.value;

        const saveBooking = {
            name,
            email,
            placeName,
            placeDescriptions,
            placePrice,
            placeImg,
            status,
            addres,
            phone,
        };
        // console.log(saveBooking);
        const uri = "https://quiet-everglades-89717.herokuapp.com/placebooking";
        fetch(uri, {
            method: "POST",
            headers: {
                "content-type": "application/json",
            },
            body: JSON.stringify(saveBooking),
        })
            .then((res) => res.json())
            .then((data) => {
                if (data.insertedId) {
                    alert("place booking successfull");
                }
            });
        e.preventDefault();
    };

    return (
        <div className="confirm-booking-form">
            <form onSubmit={handleSubmit}>
                <input
                    className="text-info"
                    type="text"
                    ref={placeNameRef}
                    placeholder="Place Name"
                    value={name || ""}
                    readOnly
                />
                <textarea
                    className="bg-info"
                    type="text"
                    ref={descRef}
                    placeholder="description"
                    value={description || ""}
                    readOnly
                />
                <input
                    className="text-info"
                    type="number"
                    ref={priceRef}
                    placeholder="Price"
                    value={price || ""}
                    readOnly
                />
                <input
                     className="text-info"
                    placeholder="Image Url"
                    ref={imgRef}
                    value={img || ""}
                    readOnly
                />
                <input
                     className="text-info"
                    type="text"
                    ref={nameRef}
                    placeholder="User Name"
                    value={user?.displayName || ""}
                    readOnly
                />
                <input
                     className="text-info"
                    type="email"
                    ref={emailRef}
                    placeholder="Email"
                    value={user?.email || ""}
                    readOnly
                />
                <input
                    
                    type="text"
                    ref={addressRef}
                    placeholder="Address"
                    required
                />
                <input
                    type="number"
                    ref={phoneRef}
                    placeholder="phone"
                    required
                />
                <input
                    type="text"
                    ref={statusRef}
                    value="pending"
                    className="disableinput"
                    readOnly
                />
                <Button
                    type="submit"
                    value="confirm booking"
                    className="confirmbutton"
                > </Button>
            </form>
        </div>
    );
};

export default PlaceForm;
